import {Form} from 'react-bootstrap';
import {Component } from 'react';
export default class Announcement extends Component

{
    handleClick=(e) =>
    {
        e.preventDefault();
        console.log('Announcement submitted')
    }
    render(){
        if(this.state.mode =='page')
    {    
    return(
    <div className="container">
        <h4 className="center">Announcement</h4>   
        <div>
            <p>Text:this.state.text}</p>
        <button onChange={this.handleChange}>
            Text
        </button>
        </div>
        </div>
        );
        }else{
            return(
                <div>
                    <p>Text:{this.state.image}</p>
                    <input 
                    onChange={this.handleChange}
                    value={this.state.inputText}
                    />
                <button onClick={this.handleClick}>Send Announcement</button>

            </div>        
    );
    }
    }
}
