import React from 'react';
import {Form} from 'react-bootstrap';
import {Component } from 'react';

function Message(props)
{
    if(props.page1)
        return <h1>Textpage</h1>
    else
        return <h1>Imagepage</h1>
}
function Text(props)
{
    return(
        <button onClick={this.handleClick}>Send Announcement</button>
    )
}
export default class Announcement extends Component{
    constructor(props) {
        super(props);

    this.state ={
    Chose:'testing',
      //  description:'dfsdh',
      //  Image:'url'
    }}
    handleChange = (e) => {
       // console.log("inside");
      this.setState({
        Title:e.target.value,
        //description :e.target.value
      });
    }
    handleClick=(e) =>
    {
        e.preventDefault();
        console.log('Announcement submitted')
    }
    render(){
    return(
    <div className="container">
        <h4 className="center">Announcement</h4>   
        <form id="text">
            <Form.Group controlId="exampleForm.ControlSelect1">
            <Form.Control as="select" onChange={this.handleChange}>
            <option id="">Select</option>
            <option id="Textpage"  value="text">Text</option>
            <option id="Imagepage" value="image">Image</option>
            </Form.Control>
            </Form.Group>
        </form>
        <p>Selected item is  {this.state.Chose}</p> 
        <div>Title:</div>
        <div>description:</div>
        <div>image:</div>   

        {/* <div>Image uRL:</div> */}

        </div>
    );
    }
}
