import React from 'react';
import {Form} from 'react-bootstrap';
import {Component } from 'react';

const handleClick=function(buttonName)
{
    switch(true)
    {
        case buttonName=="Text menu":
        this.setState({page:1})
        break
        case buttonName=="Image menu":
        this.setState({page:0})
        break
    }
}


class Announcement extends Component{
    constructor(props) {
        super(props);
    this.state ={
        page:1  
    }
}    
    render(){
    if(this.state.page==0)
    return(
        <div className="Announcement">
        <div className="container">
        <h4 className="center">Announcement</h4>   
        <Form.Control as="select" onChange={this.handleChange}>
            <option id="">Select</option>
            <option id="Textpage"  value="text">Text</option>
            <option id="Imagepage" value="image">Image</option>
        </Form.Control>
        <button onClick={this.handleClick}>Send Announcement</button>
        </div>
        </div>)
        if(this.state.page == 1)
        return(
            <div className="container">
            <div>Image uRL:</div>
            {/* <input type='text'/> */}
            </div>)
;
    }
}
